import { useContext, useState } from 'react'
// import { useHistory } from 'react-router-dom'
import { GlobalStoreContext } from '../store'

function EditSongModal () {
    const {store} = useContext(GlobalStoreContext);
    
    let song = store.markSongForEditing;

    const [title, setTitle] = useState(song? song.title : "");
    const [artist, setArtist] = useState(song? song.artist: "");
    const [youTubeId, setYouTubeId] = useState(song? song.youTubeId: "");


    let handleChangeTitle = (event) => {
        setTitle(event.target.value);
    }
    let handleChangeArtist = (event) => {
        setArtist(event.target.value);
    }
    let handleChangeLink = (event) => {
        setYouTubeId(event.target.value);
    }
    let handleCancel = (event) => {
        store.hideEditSongModal();
    }
    let handleConfirm = (event) => {
        store.saveEditedSong(title,artist, youTubeId);
    }
    return (
        <div
            class={"modal " + (store.markSongForEditing ? "is-visible" : "")} 
            id="edit-song-modal" 
            data-animation="slideInOutLeft">
                <div class="modal-root" id='verify-edit-song-root'>
                    <div class="modal-north">
                        Edit Song
                    </div>
                    <div class="modal-center">
                        <div class="modal-center-content" id="edit-song-info-div">

                                <span id="title-prompt">Title:</span>
                                <input defaultValue={title} onChange={handleChangeTitle} id="edit-song-modal-title-textfield"></input>

                                <span id="artist-prompt">Artist:</span>
                                <input defaultValue={artist} onChange={handleChangeArtist} id="edit-song-modal-artist-textfield"></input>

                                <span id="you-tube-id-prompt">YouTubeId:</span>
                                <input defaultValue={song? song.youTubeId: ""} onChange={handleChangeLink} id="edit-song-modal-youTubeId-textfield"></input>

                            
                        </div>
                    </div>
                    <div class="modal-south">
                        <input type="button" 
                            id="edit-song-confirm-button" 
                            class="modal-button" 
                            onClick={handleConfirm}
                            value='Confirm' />
                        <input type="button" 
                            id="edit-song-cancel-button" 
                            class="modal-button" 
                            onClick={handleCancel}
                            value='Cancel' />
                    </div>
                </div>
        </div>
    );
}


export default EditSongModal;
